import React from 'react'
import { useSelector } from 'react-redux';

const Counter = () => {

    const count =useSelector((state)=>state.counter.value);
    const  dispatch = useDispatch();



  return (
    <div>
      <button onClick={()=>dispatch} >
        Inctement
      </button>

      <br/>
      <br/>

      <div>
        {count}
      </div>

      <br/>
      <br/>

      <button>
        Decrement
      </button>
    </div>
  )
}

export default Counter;
