import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    value: 0
};



const CartSlice = createSlice({
    name: "counter",
    initialState, // Change 'intialStore' to 'initialState'
    reducers: {
        increment: (state) => {
            state.value += 1;
        },
        decrement: (state) => {
            state.value -= 1;
        }
    }
});


export const{increment,decrement}=CartSlice.actions;

export default CartSlice.reducer;
